Scripts.OnClick:
if (ArcanumGeneralFrame:IsVisible()) then
HideUIPanel(ArcanumGeneralFrame);
return;
else
ShowUIPanel(ArcanumGeneralFrame);
ArcanumGeneralTab_OnClick(1);
return;
end

Scripts.OnEnter:
if ArcanumConfig.BuffType == 0 then
Arcanum_BuildTooltip(this,"ANCHOR_CURSOR","Food");
else
Arcanum_BuildTooltip(this,"ANCHOR_CURSOR","Oranges");
end
Arcanum_ButtonMouseOver(1);

Scripts.OnClick:
if ArcanumConfig.BuffType == 0 then
Arcanum_BuffCast(4);
else
Arcanum_BuffCast(5);
end

if ArcanumConfig.BuffType == 0 then
Arcanum_ArmorCast(2);
else
Arcanum_ArmorCast(3);
end

if ArcanumConfig.InterfaceVersion == 1 then
Arcanum_MagicMenu(arg1);
else
if ArcanumConfig.BuffType == 0 then
Arcanum_MagicCast(6);
else
Arcanum_MagicCast(7);
end
end

if ArcanumConfig.BuffType == 0 then
Arcanum_PortalCast(41);
else
Arcanum_PortalCast(51);
end

if ArcanumConfig.BuffType == 0 then
Arcanum_PortalCast(42);
else
Arcanum_PortalCast(52);
end

if ArcanumConfig.BuffType == 0 then
Arcanum_PortalCast(43);
else
Arcanum_PortalCast(53);
end

if ArcanumConfig.BuffType == 0 then
Arcanum_PortalCast(44);
else
Arcanum_PortalCast(54);
end
